css:
@media (min-width: 1380px){
  :root {
    --max-width-header: 150rem;
  }

  .header__logo {
     width: 12rem;
   }

  .omnisend-form-6256d481a363fb0d927adb99-teaser-btn-wrapper {
    border: solid 2px #802E34;
  }


  /font h1/
  .h1, h1 {
      font-size: 2.0rem;
  }

  .header__logo-block {
    padding: 0;
    margin-right: 40px;
  }

  .header__nav-block {
    width: 785px;
  }

  .header .header__nav {
    width: 714px;
    display: flex;
    margin-right: 0px;
  }

  .d-inline-block a {
    font-size: .8rem;
  }

  .header {
    display: flex;
    justify-content: center;
  }

  .header__container .container-fluid {
    display: flex;
    padding: 0;
    margin: 0;
    max-width: var(--max-width-header);
  }

  .header__container {
    display: flex;
    max-width: var(--max-width-header);
    padding: 0;
    justify-content: center;
  }

  .header__row {
    display: flex;
    margin: 0;
    max-width: var(--max-width-header);
    justify-content: center;
  }

  .header__nav-block {
    order: 0;
    margin-right: 35px;
  }

  #search-bar {
    order: 1;
  }

  .header__nav-block {
    width: 870px;
  }

  .header .header__row .row {
    display: flex;
    justify-content: center;
  }

  .header .row {
    margin: 20px 0 20px 0;
  }

  .header form {
    width: 250px;
    margin-right: 5px;
    margin-left: 5px;
    display: flex;
    align-items: center
    justify-content: center;
  }

  .header .header__container .col-12 {
    width: 750px;
    padding: 0;
    flex: 0;
    display: flex;
  }
}

#product-actions .product__favorite {
  display: none;
}

@media (min-width: 576px)
.header__logo {
    max-height: 100px !important;
}
h5 {
      font-weight: 300;
      font-size: 1.1rem;
}
body { 
  font-family: "Comfortaa", sans-serif;
}

h1, h4{
  font-family: "Comfortaa", sans-serif;
  font-size: 1.7rem;
}

.btn-primary {
  background-color: var(--success);
  border-color: var(--success);
  font-family: "Comfortaa", sans-serif;
}

.product-card__buy.fade {
  opacity: 1 !important;
  padding-top: 15px;
}

.lead {
    font-size: 1.2rem;
    font-weight: 500;
}

.product__sku {
    display: none;
}

.top-bar__nav {
    padding: 0; 
}

main .top-bar a.top-bar__countdown {
  background-color: #000;
  color: #ffffff;
}

.info-bar__item {
    color: black;
}

.info-bar__item:hover {
    color: black;
}

.rounded-icon {
    border: 1px solid #000000;
}

main .info-bar {
    background-color: #ffffff;
}

body#page-products .mt-3{
    border: 2px dashed;
    text-align: center;
    padding: 10px;
    background: yellow;
}

.product__buy:after {
    color: #ffffff;
    content: "10% OFF na 1Âª compra com o cupom BOASVINDAS";
    font-size: 12px;
    font-weight: 500;
    white-space: pre;
    background: #000000;
    padding: 5px;
    font-weight: bold;
    margin-top: 10px;
}
@media (max-width: 767px){
.btn-primary {
  width: 100%;
}
}
